const CLOUDS = new Array(10).fill().map(() => {
  return {
    id: crypto.randomUUID(),
    x: gsap.utils.random(-0.2, 1.2),
    size: gsap.utils.random(20, 60, 1),
    speed: gsap.utils.random(4, 20, 0.1),
    delay: gsap.utils.random(-30, 0, 0.1),
    flipped: Math.random() > 0.5 ? 1 : 0,
    z: gsap.utils.random(1, 10, 1),
    light: gsap.utils.random(92, 100, 1),
  }
})

const STARS = new Array(20).fill().map(() => ({
  id: crypto.randomUUID(),
  x: gsap.utils.random(0, window.innerWidth),
  size: gsap.utils.random(1, 10, 1),
  speed: gsap.utils.random(5, 30, 0.1),
  delay: gsap.utils.random(-30, 0, 0.1),
  opacity: gsap.utils.random(0.1, 1),
}))

const App = () => {
  const bearRef = React.useRef(null)
  const astroBear = React.useRef(null)
  const moonRef = React.useRef(null)

  React.useEffect(() => {
    // Morph the balloon outline to a path
    // Reveal balloon bear...
    gsap.set(bearRef.current, {
      xPercent: -50,
      yPercent: 200,
      display: 'block',
    })
    gsap.set('.moon', {
      y: '-100vh',
      opacity: 1,
      yPercent: -50,
      xPercent: -50,
    })
    gsap.set('.astrobear', {
      left: '100%',
      xPercent: 100,
      top: '50%',
      opacity: 1,
    })

    MorphSVGPlugin.convertToPath('circle')

    const CLOUDS = document.querySelectorAll('.cloud')

    // Create a timeline that you can control the speed of
    const CLOUDS_TL = gsap.timeline()
    CLOUDS.forEach((cloud) => {
      const CLOUD_TWEEN = gsap.timeline({ repeat: -1 }).to(cloud, {
        yPercent: -100,
        y: -window.innerHeight,
        ease: 'none',
        duration: gsap.utils.random(10, 20),
      })
      CLOUDS_TL.add(CLOUD_TWEEN, 0)
    })

    CLOUDS_TL.progress(Math.random())
    CLOUDS_TL.timeScale(2)

    gsap
      .timeline()
      .to(bearRef.current, {
        yPercent: -50,
        duration: 4,
        delay: 2,
        ease: 'elastic.out(1, 0.75)',
      })
      .to(
        CLOUDS_TL,
        { timeScale: 1, duration: 3, ease: 'elastic.out(1, 0.75)' },
        '<'
      )
      .to('body', { background: 'hsl(210 20% 10%)', duration: 2, delay: 1 })
      .to(
        '.cloud',
        { opacity: 0, duration: 3, delay: () => Math.random() },
        '<'
      )
      .to('.stars', { opacity: 1, duration: 2 }, '<')
      // Hide the clouds and show the stars
      .to('.balloon__body', {
        morphSVG: '#vercel',
        delay: 1,
        ease: 'elastic.out',
        duration: 0.75,
      })
      .to(
        '.balloon__shine',
        {
          morphSVG: '#vercel-shine',
          ease: 'elastic.out',
          duration: 0.75,
        },
        '<'
      )
      .to('.balloon', {
        delay: 1,
        scale: 2,
        opacity: 0,
        duration: 0.1,
        transformOrigin: '50% 50%',
        onStart: () => {
          POP.pause()
          POP.currentTime = 0
          POP.play()
        },
      })
      .to(
        '.bear',
        {
          y: '200vh',
          duration: 0.5,
        },
        '<'
      )
      .to('.moon', {
        y: 0,
        duration: 4,
      })
      .to('.astrobear', {
        xPercent: -600,
        yPercent: -600,
        duration: 4,
        ease: 'none'
      })
      .to(['.stars', '.moon__shade', '.moon__crater'], {
        opacity: 0,
        duration: 1,
      })
      .to('body', {
        background: 'hsl(0 0% 0%)',
        duration: 1
      }, '<')
      .to('.moon__body', {
        fill: 'white'
      })
      .to('h1', {
        backgroundPosition: '0% 0%',
        duration: 2,
      })
    // At this point we want to pop the balloon and unveil the moon
    // balloonRef.current.addEventListener('click', () => {
    //   console.info('pop')
    //   gsap.set(balloonRef.current, { transformOrigin: '50% 50%' })
    //   POP.pause()
    //   POP.currentTime = 0
    //   POP.play()
    //   gsap.timeline()
    //     .to(balloonRef.current, {
    //       scale: 2,
    //       opacity: 0,
    //       duration: 0.1,
    //       transformOrigin: '50% 50%',
    //     })
    //     .to(bearRef.current, {
    //       y: '100vh',
    //       yPercent: -100,
    //       duration: 1,
    //       onComplete: () => {
    //         if (onFinish) onFinish(id)
    //       }
    //     })
  }, [])

  return (
    <>
      <BalloonBear main innerRef={bearRef} />
      {CLOUDS.map((cloud) => {
        return <Cloud key={cloud.id} {...cloud} />
      })}
      <div className="stars">
        {STARS.map((star) => {
          return (
            <div
              className="star"
              key={star.id}
              style={{
                '--size': star.size,
                '--speed': star.speed,
                '--delay': star.delay,
                '--opacity': star.opacity,
                '--x': star.x,
              }}
            />
          )
        })}
      </div>
      <Moon innerRef={moonRef} />
      <AstroBear innerRef={astroBear} />
      <h1>vercel deploy --jhey</h1>
    </>
  )
}

render(<App />, document.querySelector('#app'))
